import React from 'react';
import ReactDOM from 'react-dom';

var style = {
  backgroundColor: 'orange',
  color: 'white',
  fontFamily: 'Arial'
}

/*
const title = React.createElement(
  'h1',
  {id: 'title', className: 'header', style: style},
  'Hello World'
)
*/

const title = React.createElement(
  'ul',
  {id: 'title', className: 'header', style: style},
  React.createElement(
    'li',
    {},
    '1 item'
  )
)

ReactDOM.render(
  title,
  document.getElementById('root')
)
