import React from 'react';
import ReactDOM from 'react-dom';

var style = {
  backgroundColor: 'orange',
  color: 'white',
  fontFamily: 'Arial'
}

var li1 = React.createElement('li', {id: 'li1'}, '1 item');
var li2 = React.createElement('li', {id: 'li2'}, '2 item');
var li3 = React.createElement('li', {id: 'li3'}, '3 item');
var li4 = React.createElement('li', {id: 'li4'}, '4 item');
var li5 = React.createElement('li', {id: 'li5'}, '5 item');

const title = React.createElement(
  'ul',
  {id: 'title', className: 'header', style: style},
  li1,
  li2,
  li3,
  li4,
  li5
)

ReactDOM.render(
  title,
  document.getElementById('root')
)

